import React, { Component } from 'react';
const db = require('./database/connection'); 
class App extends Component {

  state = {
    queryResult: ''
  }

  componentDidMount() {
    db().connect(function(err){
      if(err)
      throw err;
      db.query("select * from cidades", function(err, result, fields) {
        if(err)
        throw err;
        this.setState({
          queryResult: result
        });
      });
    });
  }

  render() {
    return (
      <div className="App">
        <p>{this.state.queryResult}</p>
      </div>
    );
  }
}

export default App;
