var mysql = require('mysql');

var connMySQL = function () {
	return mysql.createConnection({
    host: "localhost",
    user: "root",
    password: "root",
    database: "mvp"
	});
}

module.exports = function () {
  console.log('conectado ao db');
	return connMySQL;
}
