import React, { Component } from 'react';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import { withStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import MenuItem from '@material-ui/core/MenuItem';
import { Button } from '@material-ui/core';

const styles = theme => ({
    marginRight: 15
});    

const baseUrl = 'http://localhost:3000/';

class AddPessoa extends Component { 
    
    state = {
        cidades: [], cidade: '',
        estadosCivis: [], estadoCivil: '',
        faixasEtarias: [], 
        faixaEtaria: '',
        fontes: [], fonte:'',
        instrucoes: [], instrucao:'',
        municipios: [], municipio:'',
        posicoesTrabalho: [], posicaoTrabalho:'',
        profissoes: [], profissao:'',
        rendas: [], renda:'',
        ufs: [], uf:''
    }

    componentDidMount() {
        return axios.all([
                     axios.get(`${baseUrl}cidades`),
                     axios.get(`${baseUrl}estados_civis`),
                     axios.get(`${baseUrl}faixas_etarias`),
                     axios.get(`${baseUrl}fontes`),
                     axios.get(`${baseUrl}instrucoes`),
                     axios.get(`${baseUrl}municipios`),
                     axios.get(`${baseUrl}posicoes_trabalho`),
                     axios.get(`${baseUrl}profissoes`),
                     axios.get(`${baseUrl}rendas`),
                     axios.get(`${baseUrl}ufs`),
                    ])
                    .then(axios.spread((cidadesRes,
                                        estadosCivisRes,
                                        faixasEtariasRes,
                                        fontesRes,
                                        instrucoesRes,
                                        municipiosRes,
                                        posicoesTrabalhoRes,
                                        profissoesRes,
                                        rendasRes,
                                        ufsRes) => {
                            
                        this.setState({
                            cidades: cidadesRes.data,
                            estadosCivis: estadosCivisRes.data,
                            faixasEtarias: faixasEtariasRes.data,
                            fontes: fontesRes.data,
                            instrucoes: instrucoesRes.data,
                            municipios: municipiosRes.data,
                            posicoesTrabalho: posicoesTrabalhoRes.data,
                            profissoes: profissoesRes.data,
                            rendas: rendasRes.data,
                            ufs: ufsRes.data    
                        })

                    }));                      
    };

    selectCidade = () => (
        this.state.cidades.map( cidade => (
            <MenuItem key={cidade.codigo_cidade} value={cidade.nome_cidade}>
                {cidade.nome_cidade}
            </MenuItem>
        ))
    )
    
    selectUf = () => (
        this.state.ufs.map( uf => (
          <MenuItem key={uf.codigo_UF} value={uf.nome_UF}>
            {uf.nome_UF}
          </MenuItem>  
        ))
    )        

    selectFaixaEtaria = () => (
        this.state.faixasEtarias.map( faixaEtaria => (
            <MenuItem key={faixaEtaria.codigo_faixa_etaria}>
                {faixaEtaria.descricao_faixa_etaria}
            </MenuItem>
        ))
    )        
    
    selectFontes = () => (
        this.state.fontes.map(fonte => (
            <MenuItem key={fonte.codigo_fonte}>
                {fonte.descricao_fonte}
            </MenuItem>
        ))
    )

    // selectEstadoCivil = () => ()

    render() {
        return(
            <Dialog
            open={this.props.open}
            >
                <DialogTitle>Adicionar</DialogTitle>
                <DialogContent>
                <DialogContentText>
                    Preencha os campos abaixo para adicionar uma nova pessoa
                </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        id="nome"
                        label="Nome"
                        type="text"
                        fullWidth
                    />
                    <TextField
                        margin="dense"
                        id="cpf"
                        label="CPF"
                        type="text"
                        fullWidth
                    />
                    <TextField
                        margin="dense"
                        id="email"
                        label="Email"
                        type="email"
                        fullWidth
                    />
                    <TextField
                        margin="dense"
                        id="telefone"
                        label="Telefone"
                        type="text"
                        fullWidth
                    />
                    <TextField
                        margin="dense"
                        id="celular"
                        label="Celular"
                        type="text"
                        fullWidth
                    />  
                    <TextField
                        margin="dense"
                        id="cep"
                        label="CEP"
                        type="text"
                        fullWidth
                    />
                    <TextField
                        select
                        value = {this.state.uf}
                        onChange={(event) => {
                            this.setState({uf: event.target.value});
                        }}
                        margin="dense"
                        id="uf"
                        label="UF"
                        type="text"
                        fullWidth
                    >
                        {this.selectUf()}
                    </TextField>
                    <TextField
                        select
                        value = {this.state.faixaEtaria}
                        onChange={(event) => {
                            this.setState({faixaEtaria: event.target.value});
                        }}
                        margin="normal"
                        id="faixaEtaria"
                        label="Faixa Etária"
                        type="text"
                        fullWidth
                    >
                        {this.selectFaixaEtaria()}
                    </TextField>
                    <TextField
                        select
                        value = {this.state.cidade}
                        onChange={(event) => {
                            this.setState({cidade: event.target.value});
                            }}
                        margin="dense"
                        id="cidade"
                        label="Cidade"
                        type="text"
                        fullWidth
                    >
                        {this.selectCidade()}
                    </TextField>
                    <TextField
                        select
                        value = {this.state.fonte}
                        onChange={(event) => {
                            this.setState({fonte: event.target.value});
                            }}
                        margin="dense"
                        id="fontes"
                        label="Fontes"
                        type="text"
                        fullWidth
                    >
                        {this.selectFontes()}
                    </TextField>
                    <TextField
                        className = {styles.marginRight}
                        margin="dense"
                        id="municipio"
                        label="Município"
                        type="text"
                        fullWidth
                    />
                    <TextField
                        className = {styles.marginRight}
                        margin="dense"
                        id="sexo"
                        label="Sexo"
                        type="text"
                        fullWidth
                    />
                    <TextField
                        className = {styles.marginRight}
                        InputLabelProps={{ shrink: true }}
                        margin="normal"
                        id="data_nascimento"
                        label="Data de nascimento"
                        type="date"
                        fullWidth
                    />       
                </DialogContent>
                <DialogActions>
                <Button onClick={() => this.props.close(false)} color="primary">
                    Cancelar
                </Button>
                <Button onClick={this.handleClose} color="primary">
                    Adicionar
                </Button>
                </DialogActions>
          </Dialog>
        );
    }
}

export default withStyles(styles)(AddPessoa);
