import React, {
  Component
} from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import MenuItem from "@material-ui/core/MenuItem";
import {
  Button
} from "@material-ui/core";
import Snackbar from "@material-ui/core/Snackbar";
import MySnackbarContentWrapper from "../MySnackbarContent/MySnackbarContent";

window.__MUI_USE_NEXT_TYPOGRAPHY_VARIANTS__ = true;

const baseUrl = "http://localhost:3000/";

const possuiCarro = [{
    codigo: 1,
    valor: "Sim"
  },
  {
    codigo: 0,
    valor: "NÃ£o"
  }
];

class AddPessoa extends Component {
  state = {
    openSnack: false,
    snackBarVariant: "",
    snackMessage: "",

    cidades: [],
    cidade: "",
    estadosCivis: [],
    estadoCivil: "",
    faixasEtarias: [],
    faixaEtaria: "",
    fontes: [],
    fonte: "",
    instrucoes: [],
    instrucao: "",
    municipios: [],
    municipio: "",
    posicoesTrabalho: [],
    posicaoTrabalho: "",
    profissoes: [],
    profissao: "",
    rendas: [],
    renda: "",
    ufs: [],
    uf: "",
    possui_carro: "",

    inputs: {
      codigo_fonte: "",
      cpf: "",
      nome: "",
      email: "",
      telefone: "",
      celular: "",
      cep: "",
      codigo_UF: "",
      codigo_cidade: "",
      codigo_municipio: "",
      sexo: "",
      dataNascimento: "",
      codigo_faixa_etaria: "",
      codigo_estado_civil: "",
      classeSocial: "",
      codigo_instrucao: "",
      codigo_renda: "",
      codigo_profissao: "",
      codigo_posicao_trabalho: "",
      imovel: "",
      funcionario: "",
      moradia: "",
      possuiCarro: "",
      raca: ""
    }
  };

  componentDidMount() {

    return axios
      .all([
        axios.get(`${baseUrl}cidades`),
        axios.get(`${baseUrl}estados_civis`),
        axios.get(`${baseUrl}faixas_etarias`),
        axios.get(`${baseUrl}fontes`),
        axios.get(`${baseUrl}instrucoes`),
        axios.get(`${baseUrl}municipios`),
        axios.get(`${baseUrl}posicoes_trabalho`),
        axios.get(`${baseUrl}profissoes`),
        axios.get(`${baseUrl}rendas`),
        axios.get(`${baseUrl}ufs`)
      ])
      .then(
        axios.spread(
          (
            cidadesRes,
            estadosCivisRes,
            faixasEtariasRes,
            fontesRes,
            instrucoesRes,
            municipiosRes,
            posicoesTrabalhoRes,
            profissoesRes,
            rendasRes,
            ufsRes
          ) => {
            this.setState({
              cidades: cidadesRes.data,
              estadosCivis: estadosCivisRes.data,
              faixasEtarias: faixasEtariasRes.data,
              fontes: fontesRes.data,
              instrucoes: instrucoesRes.data,
              municipios: municipiosRes.data,
              posicoesTrabalho: posicoesTrabalhoRes.data,
              profissoes: profissoesRes.data,
              rendas: rendasRes.data,
              ufs: ufsRes.data
            }); {
              console.log(this.state.cidades);
            }
          }
        )
      );
  }

  selectCidade = () =>
    this.state.cidades.map(cidade => ( <
      MenuItem key = {
        cidade.codigo_cidade
      }
      value = {
        cidade.nome_cidade
      } > {
        cidade.nome_cidade
      } <
      /MenuItem>
    ));

  selectUf = () =>
    this.state.ufs.map(uf => ( <
      MenuItem key = {
        uf.codigo_UF
      }
      value = {
        uf.nome_UF
      } > {
        uf.nome_UF
      } <
      /MenuItem>
    ));

  selectFaixaEtaria = () =>
    this.state.faixasEtarias.map(faixaEtaria => ( <
      MenuItem key = {
        faixaEtaria.codigo_faixa_etaria
      }
      value = {
        faixaEtaria.descricao_faixa_etaria
      } >
      {
        faixaEtaria.descricao_faixa_etaria
      } <
      /MenuItem>
    ));

  selectFontes = () =>
    this.state.fontes.map(fonte => ( <
      MenuItem key = {
        fonte.codigo_fonte
      }
      value = {
        fonte.descricao_fonte
      } > {
        fonte.descricao_fonte
      } <
      /MenuItem>
    ));

  selectEstadoCivil = () =>
    this.state.estadosCivis.map(estadoCivil => ( <
      MenuItem key = {
        estadoCivil.codigo_estado_civil
      }
      value = {
        estadoCivil.descricao_estado_civil
      } >
      {
        estadoCivil.descricao_estado_civil
      } <
      /MenuItem>
    ));

  selectInstrucoes = () =>
    this.state.instrucoes.map(instrucao => ( <
      MenuItem key = {
        instrucao.codigo_instrucao
      }
      value = {
        instrucao.descricao_instrucao
      } >
      {
        instrucao.descricao_instrucao
      } <
      /MenuItem>
    ));

  selectMunicipios = () =>
    this.state.municipios.map(municipio => ( <
      MenuItem key = {
        municipio.codigo_municipio
      }
      value = {
        municipio.nome_municipio
      } >
      {
        municipio.nome_municipio
      } <
      /MenuItem>
    ));

  selectPosicoesTrabalho = () =>
    this.state.posicoesTrabalho.map(posicaoTrab => ( <
      MenuItem key = {
        posicaoTrab.codigo_posicao_trabalho
      }
      value = {
        posicaoTrab.descricao_posicao_trabalho
      } >
      {
        posicaoTrab.descricao_posicao_trabalho
      } <
      /MenuItem>
    ));

  selectProfissoes = () =>
    this.state.profissoes.map(profissao => ( <
      MenuItem key = {
        profissao.codigo_profissao
      }
      value = {
        profissao.descricao_profissao
      } >
      {
        profissao.descricao_profissao
      } <
      /MenuItem>
    ));

  selectRendas = () =>
    this.state.rendas.map(renda => ( <
      MenuItem key = {
        renda.codigo_renda
      }
      value = {
        renda.descricao_renda
      } > {
        renda.descricao_renda
      } <
      /MenuItem>
    ));

  selectSexo = () => ["M", "F"].map(item => ( <
    MenuItem key = {
      item
    }
    value = {
      item
    } > {
      item
    } <
    /MenuItem>
  ));

  selectClasse = () => ["A", "B", "C", "D", "E"].map(item => ( <
    MenuItem key = {
      item
    }
    value = {
      item
    } > {
      item
    } <
    /MenuItem>
  ));

  selectPossuiCarro = () =>
    possuiCarro.map(item => ( <
      MenuItem key = {
        item.codigo
      }
      value = {
        item.codigo
      } > {
        item.valor
      } <
      /MenuItem>
    ));

  handleSnackClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    this.setState({
      openSnack: false
    });
  };

  addButton = () => {
    const urlRequest = this.props.isEdit ?
      `${baseUrl}pessoas/${this.props.id}` :
      `${baseUrl}pessoas/cadastrar`;

    axios
      .post(urlRequest, {
        codigo_fonte: this.state.codigo_fonte,
        cpf: this.state.cpf,
        nome: this.state.nome,
        email: this.state.email,
        telefone: this.state.telefone,
        celular: this.state.celular,
        cep: this.state.cep,
        codigo_UF: this.state.codigo_UF,
        codigo_cidade: this.state.codigo_cidade,
        codigo_municipio: this.state.codigo_municipio,
        sexo: this.state.sexo,
        data_nascimento: this.state.dataNascimento,
        codigo_faixa_etaria: this.state.codigo_faixa_etaria,
        codigo_estado_civil: this.state.codigo_estado_civil,
        classe_social: this.state.classeSocial,
        codigo_instrucao: this.state.codigo_instrucao,
        codigo_renda: this.state.codigo_renda,
        codigo_profissao: this.state.codigo_profissao,
        codigo_posicao_trabalho: this.state.codigo_posicao_trabalho,
        imovel: this.state.imovel,
        funcionario: this.state.funcionario,
        moradia: this.state.moradia,
        possui_carro: this.state.possuiCarro,
        raca: this.state.raca
      })
      .then(res => {
        if (res.status === 200) {
          this.props.close(true);
        }
      })
      .catch(error => {
        if (error.response.status === 418 || error.response.status === 404) {
          this.setState({
            openSnack: true,
            snackBarVariant: "warning",
            snackMessage: "preencha todos os campos adequadamente"
          });
        }
        if (error.response.status === 500) {
          this.setState({
            openSnack: true,
            snackBarVariant: "error",
            snackMessage: "Erro interno, por favor tente novamente"
          });
        }
        console.log(error.response.data);
        console.log(error.response.status);
        console.log(error.response.headers);
      });
  };

  render() {

    const titulo = this.props.isEdit ? `editar` : `adicionar`

    return ( <
      Dialog open = {
        this.props.open
      } >
      <
      DialogTitle > {
        titulo
      } < /DialogTitle> <
      DialogContent >
      <
      DialogContentText >
      Preencha os campos abaixo para {
        titulo
      }
      uma nova pessoa <
      /DialogContentText> <
      Snackbar anchorOrigin = {
        {
          vertical: "top",
          horizontal: "center"
        }
      }
      open = {
        this.state.openSnack
      }
      autoHideDuration = {
        6000
      }
      onClose = {
        this.handleSnackClose
      } >
      <
      MySnackbarContentWrapper onClose = {
        this.handleSnackClose
      }
      variant = {
        this.state.snackBarVariant
      }
      message = {
        this.state.snackMessage
      }
      /> <
      /Snackbar> <
      TextField required autoFocus value = {
        this.state.nome
      }
      onChange = {
        event => {
          this.setState({
            nome: event.target.value
          });
        }
      }
      margin = "dense"
      id = "nome"
      label = "Nome"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 100
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.cpf
      }
      onChange = {
        event => {
          this.setState({
            cpf: event.target.value
          });
        }
      }
      id = "cpf"
      label = "CPF"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 11
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.email
      }
      onChange = {
        event => {
          this.setState({
            email: event.target.value
          });
        }
      }
      id = "email"
      label = "Email"
      type = "email"
      fullWidth inputProps = {
        {
          maxLength: 40
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.telefone
      }
      onChange = {
        event => {
          this.setState({
            telefone: event.target.value
          });
        }
      }
      id = "telefone"
      label = "Telefone"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 30
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.celular
      }
      onChange = {
        event => {
          this.setState({
            celular: event.target.value
          });
        }
      }
      id = "celular"
      label = "Celular"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 30
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.cep
      }
      onChange = {
        event => {
          this.setState({
            cep: event.target.value
          });
        }
      }
      id = "cep"
      label = "CEP"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 10
        }
      }
      />

      <
      TextField required margin = "dense"
      value = {
        this.state.imovel
      }
      onChange = {
        event => {
          this.setState({
            imovel: event.target.value
          });
        }
      }
      id = "imovel"
      label = "ImÃ³vel"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 30
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.funcionario
      }
      onChange = {
        event => {
          this.setState({
            funcionario: event.target.value
          });
        }
      }
      id = "funcionario"
      label = "FuncionÃ¡rio"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 30
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.moradia
      }
      onChange = {
        event => {
          this.setState({
            moradia: event.target.value
          });
        }
      }
      id = "moradia"
      label = "Moradia"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 20
        }
      }
      /> <
      TextField required margin = "dense"
      value = {
        this.state.raca
      }
      onChange = {
        event => {
          this.setState({
            raca: event.target.value
          });
        }
      }
      id = "raca"
      label = "RaÃ§a"
      type = "text"
      fullWidth inputProps = {
        {
          maxLength: 10
        }
      }
      /> <
      TextField required select value = {
        this.state.uf
      }
      onChange = {
        event => {
          const codUf = () => {
            let u = this.state.ufs.filter(u => {
              return u.nome_UF === event.target.value;
            });
            return u[0]["codigo_UF"];
          };
          this.setState({
            codigo_UF: codUf(),
            uf: event.target.value
          });
        }
      }
      margin = "dense"
      id = "uf"
      label = "UF"
      type = "text"
      fullWidth >
      {
        this.selectUf()
      } <
      /TextField> <
      TextField select required margin = "dense"
      value = {
        this.state.classeSocial
      }
      onChange = {
        event => {
          this.setState({
            classeSocial: event.target.value
          });
        }
      }
      id = "classeSocial"
      label = "Classe social"
      type = "text"
      fullWidth >
      {
        this.selectClasse()
      } <
      /TextField> <
      TextField required select value = {
        this.state.faixaEtaria
      }
      onChange = {
        event => {
          const codFaixaEt = () => {
            let fe = this.state.faixasEtarias.filter(fe => {
              return fe.descricao_faixa_etaria === event.target.value;
            });
            return fe[0]["codigo_faixa_etaria"];
          };
          this.setState({
            codigo_faixa_etaria: codFaixaEt(),
            faixaEtaria: event.target.value
          });
        }
      }
      margin = "dense"
      id = "faixaEtaria"
      label = "Faixa EtÃ¡ria"
      type = "value"
      fullWidth >
      {
        this.selectFaixaEtaria()
      } <
      /TextField> <
      TextField required select value = {
        this.state.cidade
      }
      onChange = {
        event => {
          const codCidade = () => {
            let c = this.state.cidades.filter(c => {
              return c.nome_cidade === event.target.value;
            });
            return c[0]["codigo_cidade"];
          };
          this.setState({
            codigo_cidade: codCidade(),
            cidade: event.target.value
          });
        }
      }
      margin = "dense"
      id = "cidade"
      label = "Cidade"
      type = "text"
      fullWidth >
      {
        this.selectCidade()
      } <
      /TextField> <
      TextField required select value = {
        this.state.fonte
      }
      onChange = {
        event => {
