import React, { Component } from 'react';
import axios from 'axios';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Button, ListSubheader } from '@material-ui/core';

class App extends Component {

  state = {
    pessoas: []
  };

  componentDidMount() {
    console.log('did mount chamado')

    axios.get('http://localhost:3000/pessoas')
      .then(res => {
        this.setState({pessoas: res.data});
      })
  }

  deleteClick = (id) => {
    axios.delete(`http://localhost:3000/pessoas/${id}/apagar`)
         .then(res => {
           this.setState(prevState => {
             return {
               pessoas: prevState.pessoas.filter(pessoa => {
                 return pessoa.numero_registro !== id;
               })
             };
           });
         }).catch(err => {
          console.log(err);
        });
  }

  render() {

    const styles = theme => ({
      root: {
        flexGrow: 1,
        maxWidth: 752,
      },
      demo: {
        backgroundColor: theme.palette.background.paper,
      },

    });


    return (
      <div className={styles.root}>

      <Grid container 
            direction="row"
            justify="center"
            alignItems="center">
          <Grid item xs={12} md={6}>
            <Typography variant="h6" align = "center">
              Lista de Pessoas
            </Typography>
              <List>
                {this.state.pessoas.map(pessoa =>
                        <ListItem button key = {pessoa.numero_registro}>
                        <ListItemText
                          primary={pessoa.nome}
                        />
                        <Button>
                            <DeleteIcon  onClick={() => this.deleteClick(pessoa.numero_registro)} />
                        </Button>
                        <Button>
                            <EditIcon/>
                        </Button>
                      </ListItem>
                  )}
              </List>
          </Grid>
      </Grid>
    </div>
    );
  }
}

export default App;
