import React, { Component } from "react";
import axios from "axios";
import Grid from "@material-ui/core/Grid";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Typography from "@material-ui/core/Typography";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import { Button } from "@material-ui/core";
import AddPessoa from "./components/AddPessoa/AddPessoa";
import ProgressBar from "./components/ProgressBar/ProgressBar";
import Snackbar from "@material-ui/core/Snackbar";
import MySnackbarContentWrapper from "./components/MySnackbarContent/MySnackbarContent";
class App extends Component {
  state = {
    pessoas: [],
    showDialog: false,
    showProgressBar: true,
    openSnack: false,
    snackBarVariant: "",
    snackMessage: ""
  };

  componentDidMount() {
    axios
      .get("http://localhost:3000/pessoas")
      .then(res => {
        this.setState({ pessoas: res.data });
      })
      .then(this.setState({ showProgressBar: false }));
  }

  deleteClick = id => {
    axios
      .delete(`http://localhost:3000/pessoas/${id}/apagar`)
      .then(res => {
        this.setState(prevState => {
          return {
            pessoas: prevState.pessoas.filter(pessoa => {
              return pessoa.numero_registro !== id;
            })
          };
        });
      })
      .catch(err => {
        console.log(err);
      });
  };

  handleSnackClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    this.setState({ openSnack: false });
  };

  render() {
    return (
      <div>
        <Grid container direction="row" justify="center" alignItems="center">
          <Grid item xs={12} md={4}>
            <Typography variant="h6" align="center">
              Lista de Pessoas
            </Typography>
            <div
              style={{
                textAlign: "center",
                display: this.state.showProgressBar ? "block" : "none"
              }}
            >
              <ProgressBar />
            </div>
            <List>
              {this.state.pessoas.map(pessoa => (
                <ListItem button key={pessoa.numero_registro}>
                  <ListItemText primary={pessoa.nome} />
                  <Button>
                    <DeleteIcon
                      onClick={() => this.deleteClick(pessoa.numero_registro)}
                    />
                  </Button>
                  <Button>
                    <EditIcon />
                  </Button>
                </ListItem>
              ))}
            </List>
            <Grid container direction="row" align="center">
              <Button
                onClick={() => this.setState({ showDialog: true })}
                fullWidth={true}
                color="primary"
              >
                Adicionar
              </Button>
            </Grid>
          </Grid>
        </Grid>
        <AddPessoa
          open={this.state.showDialog}
          close={(v) => {
            if(v) {
              this.setState({
                openSnack: true,
                snackBarVariant: "success",
                snackMessage: "Pessoa adicionada com succeso"
              });
            }
            this.setState({showDialog: false});
            // axios.get("http://localhost:3000/pessoas").then(res => {
            //   this.setState({ pessoas: res.data });
            // });
          }}
        />
        <Snackbar
          anchorOrigin={{
            vertical: "center",
            horizontal: "center"
          }}
          open={this.state.openSnack}
          autoHideDuration={4000}
          onClose={this.handleSnackClose}
        >
          <MySnackbarContentWrapper
            onClose={this.handleSnackClose}
            variant={this.state.snackBarVariant}
            message={this.state.snackMessage}
          />
        </Snackbar>
      </div>
    );
  }
}

export default App;
