import React, { Component } from 'react';
import axios from 'axios';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import { Button } from '@material-ui/core';
// import Lista from './components/Lista/Lista';

class App extends Component {

  state = {
    pessoas: []
  };

  componentDidMount() {
    console.log('did mount chamado')

    axios.get('http://localhost:3000/pessoas')
      .then(res => {
        this.setState({pessoas: res.data});
      })
  }

  handleClick = () => {
    alert('fui clicado!');
  }

  render() {

    const styles = theme => ({
      root: {
        flexGrow: 1,
        maxWidth: 752,
      },
      demo: {
        backgroundColor: theme.palette.background.paper,
      },
      title: {
        margin: `${theme.spacing.unit * 4}px 0 ${theme.spacing.unit * 2}px`,
      },
      icon:{
          margin: 20
      }
    });

    const { classes } = this.state;

    return (
      <div className={styles.root}>

      <Grid container spacing={16}>
          <Grid item xs={12} md={6}>
            <Typography variant="h6" className={styles.title}>
              Lista de Pessoas
            </Typography>
            <div className={styles.demo}>
              <List>
                {this.state.pessoas.map(pessoa =>
                    <ListItem key = {pessoa.numero_registro}>
                      <ListItemText
                        primary={pessoa.nome}
                      />
                      <ListItemSecondaryAction>
                        <IconButton aria-label="Deletar">
                          <DeleteIcon className = {styles.icon} onClick={this.handleClick} />
                        </IconButton>
                      </ListItemSecondaryAction>
                      <Button>
                          <EditIcon/>
                      </Button>
                    </ListItem>
                  )}
              </List>
            </div>
          </Grid>
      </Grid>
    </div>
      // <div>
      //       {<div>{
      //             this.state.pessoas.map(pessoa => 
      //               <List key = {pessoa.numero_registro} >{pessoa.nome}</List>
      //             )}
      //       </div>}
      // </div>
    );
  }
}

export default App;
