import React, { Component } from 'react';
class App extends Component {

  state = {
    queryResult: 'teste'
  }

  componentDidMount() {

    var mysql = require('mysql');

    var connection = mysql.createConnection({
      host: 'localhost',
      user: 'root',
      password: 'root',
      database: 'mvp'
    });

    connection();

    connection.query('SELECT * FROM CIDADES', function(err, rows) {

      if (err) {
          console.error('error connecting: ' + err.stack);
          return;
        }
      console.log('connected!');
      });
  }

  render() {
    return (
      <div className="App">
        <p>{this.state.queryResult}</p>
      </div>
    );
  }
}

export default App;
