import React, { Component } from 'react';
import axios from 'axios';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
// import Lista from './components/Lista/Lista';

const styles = theme => ({
  root: {
    flexGrow: 1,
    maxWidth: 752,
  },
  demo: {
    backgroundColor: theme.palette.background.paper,
  },
  title: {
    margin: `${theme.spacing.unit * 4}px 0 ${theme.spacing.unit * 2}px`,
  },
  icon:{
      margin: 20
  }
});

class App extends Component {
  state = {
    pessoas: []
  };

  componentDidMount() {
    console.log('did mount chamado')

    axios.get('http://localhost:3000/pessoas')
      .then(res => {
        this.setState({pessoas: res.data});
      })
  }

  render() {

    const { classes } = this.props;

    return (
      <div className={classes.root}>

      <Grid container spacing={16}>
          <Grid item xs={12} md={6}>
            <Typography variant="h6" className={classes.title}>
              Lista de Pessoas
            </Typography>
            <div className={classes.demo}>
              <List>
                  <ListItem key = {pessoa.numero_registro}>
                    <ListItemText
                      primary={pessoa.nome}
                    />
                    <ListItemSecondaryAction>
                      <IconButton aria-label="Deletar">
                        <DeleteIcon onClick={this.handleClick} />
                      </IconButton>
                    </ListItemSecondaryAction>
                    <Button className={classes.icon}>
                        <EditIcon/>
                    </Button>
                  </ListItem>
              </List>
            </div>
          </Grid>
      </Grid>
    </div>
      // <div>
      //       {<div>{
      //             this.state.pessoas.map(pessoa => 
      //               <List key = {pessoa.numero_registro} >{pessoa.nome}</List>
      //             )}
      //       </div>}
      // </div>
    );
  }
}

export default App;
