import React, { Component } from 'react';
class App extends Component {

  state = {
    queryResult: 'teste'
  }

  componentDidMount() {

    var mysql = require('mysql');

    var connMySQL = async function () {
      return mysql.createConnection({
        host: "localhost",
        user: "root",
        password: "root",
        database: "mvp"
      });
    }

    connMySQL.connect(function(err) {
      if (err) throw err;
      connMySQL.query('select * from cidades', function (err, result, fields) {
        if (err) throw err;
        console.log(result);
      });
    });
  }

  render() {
    return (
      <div className="App">
        <p>{this.state.queryResult}</p>
      </div>
    );
  }
}

export default App;
