    console.log('did mount chamado')

    axios.get(`http://localhost:3000/pessoas`)
      .then(res => {
        const persons = res.data;
        console.log(persons);
      })
  }

  render() {
    return (
      <div className="App">
        <ul></ul>
      </div>
    );
  }
}

export default App;


